package hashcode;

import java.util.Objects;

public class User {

    /*
    * Simply put, hashCode() returns an integer value, generated by a hashing algorithm
     * Objects that are equal (according to their equals()) must return the same hash code.
     *  Different objects do not need to return different hash codes.
     *
     *
     * Whenever it is invoked on the same object more than once during an execution of a Java application,
     *  hashCode() must consistently return the same value,
     * provided no information used in equals comparisons on the object is modified.
     *  This value doesnâ€™t need to stay consistent from
     * one execution of an application to another execution of the same application.
    * */
    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        User user = (User) o;
        return id == user.id && Objects.equals(name, user.name);
    }

    @Override
    public int hashCode() {
        return Objects.hash(id, name);
    }

    int id;
    String name;

    public static void main(String[] args) {
        User user = new User();
        user.id =10;
        user.name="Mahendra";
        System.out.println("hash code of Object "+user+" : "+ user.hashCode() );
    }
}
